import React, { Component, Fragment } from "react";
//import { Link } from "react-router-dom";
import Footer from "../footer/Footer";

import "./Nous.scss";

class Nous extends Component {
  state = {
    show: false
  };

  //ouvre/ferme la popup
  toggleDialog = () => {
    this.setState({ show: !this.state.show });
  };

  render() {
    return (
      <Fragment>
        <h1>
          À Propos de <span>SneakerBis</span>
        </h1>
        <div class="page_subtitle"> La marketplace Sneakers française</div>
        <h2>SneakersHub c'est quoi? </h2>
        <p class="large" style="text-align: center; margin-top: -30px;">
          Tout d'abord, SneakersHub est une plateforme d'achat vente dédiée aux
          sneakers en France. Notre ambition est de devenir la place de marché
          la plus sécurisée et la plus simplifiée dans le milieu de la sneakers.
          Ensuite, chaque produit mis en vente sur le site est authentifié pour
          que la qualité et la légitimité soit toujours au rendez-vous. Aussi,
          SneakersHub est une marketplace de sneakers réservée uniquement à la
          France. Enfin, vous retrouverez dans notre{" "}
          <a href="https://sneakershub.fr/catalogue-sneakers">
            catalogue sneakers
          </a>{" "}
          toutes les plus grandes marques. Nike, Jordan, Adidas, Yeezy, New
          Balance, Converse, Puma, Airwalk... A vous de faire votre choix!{" "}
        </p>
        <Footer />
      </Fragment>
    );
  }
}

export default Nous;
