import React, { Fragment } from "react";
import { Link } from "react-router-dom";

import "./Recherche.scss";

class Recherche extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      visible: 6,
      error: false,
      brands: []
    };

    this.loadMore = this.loadMore.bind(this);
  }
  loadMore() {
    this.setState(prev => {
      return { visible: prev.visible + 6 };
    });
  }

  brandsFilter() {
    this.setState(filtre => {
      return { brands: filtre.brands };
    });
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err))
      // fetch("/api/product")
      .then(res => {
        console.log(res);
        res.json();
      })
      .then(res => {
        this.setState({
          items: res
        });
      })
      .catch(error => {
        console.error(error);
        this.setState({
          error: true
        });
      });
    //ON AFFICHE LE NOM DES MARQUES POUR LE FILTRE
    //this.routeParam = this.props.match.params.id;
    sessionStorage.setItem("id_brand", this.routeParam);
    const currentBrandId = sessionStorage.getItem("id_brand");
    this.callApi(`/api/brand/${currentBrandId}`)
      .then(response => {
        //console.log(response[0].id_brand);
        sessionStorage
          .set("currentBrandId", response[0].id_brand)
          .then(response => response.json())
          .then(this.onLoad);
      })
      .catch(err => console.log(err));
  }
  // onLoad = data => {
  //   this.setState({
  //     data: this.parseData(data)
  //   });
  // };

  callApi = async url => {
    // const items = await fetch("/api/product");
    const items = await fetch(url);
    // const brands = await fetch(`brand/:id_brand`);
    const body = await items.json();
    // const labelBrands = await brands.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
    // if (brands.status !== 200) throw Error(labelBrands.message);
    // return labelBrands;
  };

  render() {
    return (
      <div className="site">
        <div className="recherche">
          <p>DECOUVREZ NOS MODELES</p>
        </div>
        <div className="marques1">
          {this.state.brands.map(e => (
            <div className="nom" key={e.id}>
              <button
                // onClick={{
                //   pathname: `/brand/:id_brand/${e.id_brand}`,
                //   state: { newBrand: e.label }
                // }}
                // onClick={this.brandsFilter}
                onClick={console.log("I'm clicked")}
              >
                GUCCI
              </button>
            </div>
          ))}

          <div className="nom">
            <a href="https://www.nike.com/fr/fr_fr/c/jordan" target="_blank">
              JORDAN
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr" target="_blank">
              NIKE
            </a>
          </div>
          <div className="nom">
            <a href="https://www.adidas.fr/" target="_blank">
              ADIDAS
            </a>
          </div>
          <div className="nom">
            <a
              href="https://yeezysupply.com/collections/new-arrivals-footwear"
              target="_blank"
            >
              YEEZY
            </a>
          </div>
          <div className="nom">
            <a href="https://eu.puma.com/fr/fr/home" target="_blank">
              PUMA
            </a>
          </div>
          <div className="nom">
            <a
              href="https://www.chanel.com/fr_FR/mode/produits/chaussures.html"
              target="_blank"
            >
              CHANEL
            </a>
          </div>
        </div>
        <div className="marques">
          <Fragment>
            {this.state.items
              .slice(0, this.state.visible)
              .map((item, index) => {
                return (
                  <div className="shoes" key={item.id}>
                    <div>{item.marque}</div>
                    <div>{item.modèle}</div>
                    <figure>
                      <img
                        src={item.photo_1}
                        alt="afficher les sneakers"
                        className="sizePic"
                      />
                    </figure>
                    <div>{item.prix} €</div>
                  </div>
                );
              })}
          </Fragment>
        </div>
        {this.state.visible < this.state.items.length && (
          <button onClick={this.loadMore} type="button" className="plus">
            VOIR PLUS
          </button>
        )}
      </div>
    );
  }
}

export default Recherche;
