import React, { Fragment } from "react";

import Button from "../../components/Button/Button";
import "./Recherche.scss";

class Recherche extends React.Component {
  state = {
    response: []
  };

  //récupère le nom des FT
  componentDidMount() {
    this.callApi()
      .then(response => {
        this.setState({ response });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/product");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    return (
      <div className="site">
        <div className="recherche">
          <p>LES PLUS RECHERCHES</p>
        </div>
        <div className="marques1">
          <div className="nom">
            <a href="https://www.gucci.com/fr/fr/" target="_blank">
              GUCCI
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr/fr_fr/c/jordan" target="_blank">
              JORDAN
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr" target="_blank">
              NIKE
            </a>
          </div>
          <div className="nom">
            <a href="https://www.adidas.fr/" target="_blank">
              ADIDAS
            </a>
          </div>
          <div className="nom">
            <a
              href="https://yeezysupply.com/collections/new-arrivals-footwear"
              target="_blank"
            >
              YEEZY
            </a>
          </div>
          <div className="nom">
            <a href="https://eu.puma.com/fr/fr/home" target="_blank">
              PUMA
            </a>
          </div>
          <div className="nom">
            <a
              href="https://www.chanel.com/fr_FR/mode/produits/chaussures.html"
              target="_blank"
            >
              CHANEL
            </a>
          </div>
        </div>

        {this.state.response.map(e => (
          <Fragment>
            <div className="marques">
              <div className="shoes">
                <div>{e.marque}</div>
                <div>{e.modèle}</div>
                <figure>
                  <img src={e.photo_1} alt="photo des sneakers" />
                </figure>
              </div>
            </div>
          </Fragment>
        ))}

        <Button
          textButton="VOIR PLUS"
          classButton="plus"
          to={{
            pathname: `/`
          }}
        />
      </div>
    );
  }
}

export default Recherche;
