import React from "react";
//import { BrowserRouter as Router } from "react-router-dom";

// import Button from "../../components/Button/Button";
import "./Recherche.scss";

class Recherche extends React.Component {
  state = {
    response: []
  };

  //récupère le nom des FT
  componentDidMount() {
    this.callApi()
      .then(response => {
        this.setState({ response });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/product");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    return (
      <div className="site">
        <span className="recherche">
          <p>LES PLUS RECHERCHES</p>
        </span>
        <div className="marques">
          <div className="nom">
            <a href="https://www.gucci.com/fr/fr/" target="_blank">
              GUCCI
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr/fr_fr/c/jordan" target="_blank">
              JORDAN
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr" target="_blank">
              NIKE
            </a>
          </div>
          <div className="nom">
            <a href="https://www.adidas.fr/" target="_blank">
              ADIDAS
            </a>
          </div>
          <div className="nom">
            <a
              href="https://yeezysupply.com/collections/new-arrivals-footwear"
              target="_blank"
            >
              YEEZY
            </a>
          </div>
          <div className="nom">
            <a href="https://eu.puma.com/fr/fr/home" target="_blank">
              PUMA
            </a>
          </div>
          <div className="nom">
            <a
              href="https://www.chanel.com/fr_FR/mode/produits/chaussures.html"
              target="_blank"
            >
              CHANEL
            </a>
          </div>
        </div>
        <div className="marques">
          <tbody className="shoes">
            {this.state.response.map(e => (
              <tr>
                <td>{e.marque}</td>
                <td>{e.modèle}</td>
                <td>
                  <img src={e.photo_1} alt="photo des sneakers" />
                </td>
              </tr>
            ))}
          </tbody>
          <figure className="shoes" className="animated flip">
            <img
              src={require("./adidas Yeezy Boost 350 V2 Semi Frozen Yellow.jpeg")}
              alt="La Jordan LA page d'accueil SneakerBis"
              className="imgRecherche"
            />
          </figure>
          <figure src="shoes">
            <img
              src={require("./adidas Yeezy Boost 350 V2 Semi Frozen Yellow.jpeg")}
              alt="La Jordan LA page d'accueil SneakerBis"
              className="imgRecherche"
            />{" "}
          </figure>
          <figure src="shoes">
            <img
              src={require("./adidas Yeezy Boost 350 V2 Semi Frozen Yellow.jpeg")}
              alt="La Jordan LA page d'accueil SneakerBis"
              className="imgRecherche"
            />
          </figure>
        </div>
      </div>
    );
  }
}

export default Recherche;
