import React, { Component, Fragment } from "react";
import Popup from "../../components/Popup/Popup";
import { Link, Route } from "react-router-dom";
import Connexion from "../../views/connexion/Connexion";

import Logo from "../Logo/Logo";
import "./Header.scss";

class Header extends Component {
  state = {
    show: false
  };

  //ouvre/ferme la popup
  toggleDialog = () => {
    this.setState({ show: !this.state.show });
  };

  render() {
    return (
      <Fragment>
        {this.state.show && <Popup toggleDialog={this.toggleDialog} />}
        <nav>
          <ul className="list">
            <Logo />
            <li>
              <Link to="/recherche" className="list">
                SHOP
              </Link>
            </li>
            <li>
              <Link to="/contact" className="list">
                CONTACT
              </Link>
            </li>
            <li>
              <a href="#" onClick={this.toggleDialog}>
                RECHERCHE
              </a>
            </li>
            <Link to="/connexion" className="list">
              CONNEXION
            </Link>
          </ul>
        </nav>
      </Fragment>
    );
  }
}

export default Header;
