import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      items: [],
      filter: "",
      message: false,
      todo: []
    };
    this.updateSearch = this.updateSearch.bind(this);
  }

  updateSearch(inputValue) {
    this.setState({
      filter: inputValue,
      value: this.state.value + inputValue
    });
    console.log("je console le this", this.state.value);
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ todo: this.state.items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    const body = await items.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            <form className="form">
              <input
                className="inputPopUp"
                type="text"
                placeholder="Recherchez un modÃ¨le"
                onChange={e => this.updateSearch(e.target.value)}
                required
              />
            </form>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
