import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      redirect: false,
      // items: [],
      filter: null
    };
    let items = [
      {
        id: 1,
        name: "Jordan",
        modèle: "11 retro"
      },
      {
        id: 2,
        name: "Puma",
        modèle: "les biens"
      },
      {
        id: 3,
        name: "Reebok",
        modèle: "Les belles"
      }
    ];
  }

  updateSearch(inputValue) {
    let filter = this.state.filter;

    this.setState({
      //filter: inputValue
      value: inputValue.target.value.substr(0, 20)
    });
  }

  handleChange(event) {
    console.log(this.state.updateSearch);
    //this.state.updateSearch(event.target.value);
    //this.setState({ search: event.target.value.substr(0, 20) });
  }

  // filter(items) {
  //   if (!this.props.filter) {
  //     return items;
  //   }
  //   return items.filter(
  //     items =>
  //       items.toLowerCase().indexOf(this.state.filter.toLowerCase()) !== -1 //>= 0
  //   );
  // }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    const body = await items.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    // let ConditionalLink = this.state.value ? Link : Fragment;
    let filteredItems = this.props.items.filter(item => {
      return (
        item.marque.toLowerCase().indexOf(this.state.value.toLowerCase()) !== -1
      );
    });
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            {this.filter(this.state.items).map(item => {
              return (
                <form className="form" key={item.id}>
                  {/* <input
                    className="inputPopUp"
                    type="text"
                    placeholder="Recherchez un modèle"
                    onChange={this.handleChange.bind(this)}
                    value={this.state.searchText}
                    //value={item.marque}
                    items={this.state.item}
                    searchText={this.state.filter}
                    required
                  /> */}
                  {filteredItems.map(item => {
                    return (
                      <input
                        value={this.state.value}
                        onChange={this.updateSearch.bind(this)}
                      >
                        {item.marque}
                      </input>
                    );
                  })}
                </form>
              );
            })}
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
