import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // value: "",
      items: [],
      currentItem: {
        text: "",
        key: ""
      },
      message: false,
      todo: []
    };
    this.updateSearch = this.updateSearch.bind(this);
  }

  updateSearch(e) {
    e.preventDefault();
    this.setState({
      //filter: e,
      currentItem: e
      //value: this.state.value + e
    });
    console.log("je console le this", e);
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ todo: this.state.items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    const body = await items.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    const filterListItems = this.state.items
      .filter(
        d => this.state.currentItem === "" || d.includes(this.state.currentItem)
      )
      .map((f, index) => <li key={index}>{f}</li>);
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            <form className="form">
              <input
                className="inputPopUp"
                value={this.state.currentItem}
                type="text"
                placeholder="Recherchez un modèle"
                onChange={e => this.updateSearch(e.target.value)}
                required
              />
              <ul>{filterListItems}</ul>
            </form>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
