  constructor(props) {
    super(props);
    this.state = {
      value: "",
      redirect: false,
      items: []
    };
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

