import React, { Fragment } from "react";
import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      redirect: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    this.setState({ value: event.target.value });

    const currentId = sessionStorage.getItem("id_ft");
    const campaign_infos = {
      nom_campagne: this.state.value,
      id_ft: currentId
    };
    const fetch_param = {
      method: "POST",
      headers: { "content-type": "application/json" },
      body: JSON.stringify(campaign_infos)
    };

    fetch("/api/campaign-name", fetch_param)
      .then(function(results) {
        return results.json();
      })
      .then(function(myresults) {});

    sessionStorage.setItem("currentCampaignName", this.state.value);
  }

  render() {
    let ConditionalLink = this.state.value ? Link : Fragment;
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            <form onSubmit={this.handleSubmit} className="form">
              <span className="textSpan">RECHERCHE</span>
              <input
                className="inputPopUp"
                value={this.state.value}
                onChange={this.handleChange}
                type="text"
                placeholder="Nom de la campagne"
                required
              />

              <ConditionalLink
                to={{
                  pathname: "/themes"
                }}
              >
                {/* <Button
                  textButton="Valider"
                  onClick={this.handleSubmit}
                  classButton="btnValider"
                /> */}
              </ConditionalLink>
            </form>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
