import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      redirect: false,
      items: [],
      //students: STUDENTS,
      filter: null
    };
  }

  updateSearch(inputValue) {
    let filter = this.state.filter;

    this.setState({
      filter: inputValue
    });
  }

  handleChange(event) {
    this.props.updateSearch(event.target.value);
  }

  filter(items) {
    if (!this.props.filter) {
      return items;
    }
    return items.filter(
      items => items.toLowerCase().indexOf(this.props.filter.toLowerCase()) >= 0
    );
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    // if (items.status !== 200) throw Error(body.message);
    // return body;
  };

  render() {
    // let ConditionalLink = this.state.value ? Link : Fragment;
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            {/* <form className="form"> */}
            {this.state.items.filter(this.props.items).map(item => (
              <input
                className="inputPopUp"
                type="text"
                placeholder="Recherchez un modÃ¨le"
                onChange={this.handleChange.bind(this)}
                value={this.props.searchText}
                searchText={this.state.filter}
                required
              />
            ))}
            {/* </form> */}
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
