import React, { Fragment } from "react";

import Button from "../../components/Button/Button";
import "./Recherche.scss";

class Recherche extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      visible: 2,
      error: false
      //response: []
    };

    this.loadMore = this.loadMore.bind(this);
  }
  loadMore() {
    this.setState(prev => {
      return { visible: prev.visible + 4 };
    });
  }
  // state = {
  //   response: []
  // };

  componentDidMount() {
    this.callApi()
      .then(items => {
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then(res => res.json())
      .then(res => {
        this.setState({
          items: res
        });
      })
      .catch(error => {
        console.error(error);
        this.setState({
          error: true
        });
      });
  }

  // callApi = async () => {
  //   const items = await fetch("/api/product")
  //     .then(res => res.json())
  //     .then(res => {
  //       this.setState({
  //         items: res
  //       });
  //     })
  //      .catch(error => {
  //         console.error(error);
  //         this.setState({
  //           error: true
  //         });
  //     const body = await items.json();
  //     if (items.status !== 200) throw Error(body.message);
  //     return body;
  // };

  loadPage = e => {
    let i = i + 10;
    console.log(i);
  };

  render() {
    // let sizeDiv = 81;
    // console.log(sizeDiv);
    return (
      <div className="site">
        <div className="recherche">
          <p>DECOUVREZ NOS MODELES</p>
        </div>
        <div className="marques1">
          <div className="nom">
            <a href="https://www.gucci.com/fr/fr/" target="_blank">
              GUCCI
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr/fr_fr/c/jordan" target="_blank">
              JORDAN
            </a>
          </div>
          <div className="nom">
            <a href="https://www.nike.com/fr" target="_blank">
              NIKE
            </a>
          </div>
          <div className="nom">
            <a href="https://www.adidas.fr/" target="_blank">
              ADIDAS
            </a>
          </div>
          <div className="nom">
            <a
              href="https://yeezysupply.com/collections/new-arrivals-footwear"
              target="_blank"
            >
              YEEZY
            </a>
          </div>
          <div className="nom">
            <a href="https://eu.puma.com/fr/fr/home" target="_blank">
              PUMA
            </a>
          </div>
          <div className="nom">
            <a
              href="https://www.chanel.com/fr_FR/mode/produits/chaussures.html"
              target="_blank"
            >
              CHANEL
            </a>
          </div>
        </div>
        <div className="marques">
          {this.state.response.map(e => (
            <Fragment>
              {this.state.items
                .slice(0, this.state.visible)
                .map((item, index) => {
                  return (
                    <div className="shoes">
                      <div>
                        {e.marque}
                        {/* {item.marque} */}
                      </div>
                      <div>{e.modèle}</div>
                      <figure>
                        <img
                          src={e.photo_1}
                          alt="afficher les sneakers"
                          className="sizePic"
                        />
                      </figure>
                    </div>
                  );
                })}
            </Fragment>
          ))}
        </div>
        {/* <Button
          textButton="VOIR PLUS"
          classButton="plus"
          to={{
            pathname: `/`
          }}
          clique={this.loadPage}
        /> */}
        {this.state.visible < this.state.items.length && (
          <button onClick={this.loadMore} type="button" className="load-more">
            Load more
          </button>
        )}
      </div>
    );
  }
}

export default Recherche;
