import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      currentItem: {
        text: "",
        key: ""
      }
    };
    this.updateSearch = this.updateSearch.bind(this);
  }

  updateSearch(e) {
    //e.preventDefault();
    this.callApi(`/api/product/${e}`)
      .then(items => {
        this.setState({ items });
        sessionStorage.clear();
        console.log("je reviens de la database", items);
      })
      .catch(err => console.log(err));
    if (this.state.currentItem !== "") {
      this.setState({
        //currentItem: e
        items: [...this.state.items, this.state.currentItem],
        currentItem: {
          text: "",
          key: ""
        }
      });
    }
    console.log(this.state.items);
  }

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        //console.log(items);
        this.setState({ items });
        sessionStorage.clear();
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    const body = await items.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    // const filterListItems = this.state.items
    //   .filter(
    //     d => this.state.currentItem === "" || d.includes(this.state.currentItem)
    //   )
    //   .map((f, index) => <li key={index}>{f}</li>);
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            <form className="form">
              {this.state.items.filter(
                d =>
                  this.state.currentItem === "" ||
                  d.includes(this.state.currentItem).map(index => {
                    return (
                      <input
                        className="inputPopUp"
                        value={this.state.currentItem}
                        type="text"
                        placeholder="Recherchez un modèle"
                        onChange={e => this.updateSearch(index.id_marque)}
                        required
                      />
                      // <ul>{index}</ul>
                    );
                  })
              )}
              {/* <input
                className="inputPopUp"
                value={this.state.currentItem}
                type="text"
                placeholder="Recherchez un modèle"
                onChange={e => this.updateSearch(e.target.value)}
                required
              />
              <ul>{filterListItems}</ul> */}
            </form>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
