import React, { Fragment } from "react";
//import { Link } from "react-router-dom";

import "./Popup.scss";

class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      filterItems: []
    };
    this.updateSearch = this.updateSearch.bind(this);
  }

  updateSearch = e => {
    //e.preventDefault();
    let searchItem = this.state.items;
    searchItem = searchItem.filter(item => {
      return item.toLowerCase().search(e.target.value.toLowerCase()) !== -1;
    });
    console.log(searchItem);
    this.callApi(`/api/product/`)
      .then(items => {
        this.setState({ items });
        sessionStorage.clear();
        console.log("je suis dans la fonction du pop-up", items);
      })
      .catch(err => console.log(err));
  };

  componentDidMount() {
    this.callApi("/api/product")
      .then(items => {
        this.setState({ items });
        sessionStorage.clear();
        console.log(items);
      })
      .catch(err => console.log(err));
  }

  callApi = async url => {
    const items = await fetch(url);
    const body = await items.json();
    if (items.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    return (
      <Fragment>
        <div className="mask">
          <div className="popup">
            <form className="form" onChange={this.updateSearch()}>
              {this.state.filterItems.map(fItem => {
                return (
                  <input
                    className="inputPopUp"
                    //value={this.state.currentItem}
                    type="text"
                    placeholder="Recherchez un modÃ¨le"
                    required
                  />
                );
              })}
              <button type="submit">Rechercher</button>
            </form>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Popup;
